import React, {useEffect} from 'react';
import {Header, Sidebar} from "../../components";
import { ScriptNavbar } from '../../helper/scriptNavbar';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProducts, deleteProduct } from '../../redux/action';
import '../../style/main.css'

const Product = () => {
    const dispatch = useDispatch();
    const products = useSelector(state => state.products);

    useEffect(()=> {
        ScriptNavbar()
        dispatch(fetchProducts())
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const handleDeleteProduct = (e) => {
        e.preventDefault();
        console.log(e, `<<< e`);
    }

    console.log(products, `<<<< products`);

  return (
    <>
    <Header />
    <Sidebar />
    <main className="product">
        <div className="product-box">
            <div className="head-bar-product">
                <p className="head-bar-title">PRODUCT</p>
                <div className="search-bar">
                    <input className="search-product" type="search" name="search" placeholder="Search Product... ." id="" />
                    <button className="search-button" >
                        <i className="fa-solid fa-magnifying-glass"></i>
                    </button>
                </div>
                <button className="create-product">
                    <Link to="/add-product">
                        <i className="fa-solid fa-plus"></i>
                        <p>CREATE</p>
                    </Link>
                </button>
            </div>
            <div className="body-list-product">
                <table className="list-product">
                    <thead>
                        <tr>
                            <td>NO.</td>
                            <td>IMAGE</td>
                            <td>NAME</td>
                            <td>CATEGORY</td>
                            <td>ACTION</td>
                        </tr>
                    </thead>
                    <tbody>
                        {products ? products.map((e) => {
                            return (
                                <tr key={e.id}>
                                    <td>{e.id}</td>
                                    <td><img src={e.gambar_produk} alt="Product" /></td>
                                    <td>{e.nama_produk}</td>
                                    <td>{e.kategori_produk}</td>
                                    <td>
                                        <Link to="/src/page/product-create-page.html">
                                            <button className="edit"><i className="fa-solid fa-pen"></i></button>
                                        </Link>
                                        <button onClick={(e)=> handleDeleteProduct({e})} className="delete"><i className="fa-solid fa-trash"></i></button>
                                    </td>
                                </tr>
                            )
                        }) : null}
                    </tbody>
                </table>
            </div>
        </div>
        <footer>
            Admin Server Site &#169; 2022, Tokopaedi
        </footer>
    </main>
    </>
  )
}

export default Product
