import { FETCH_PRODUCT } from '../utils/constants';

const initState = {
    products: [],
}

const reducer = (state = initState, action) => {
    switch (action.type) {
        case FETCH_PRODUCT:
            return {
                ...state,
                menus: action.payload
            }
        default:
            return state;
    }
}

export default reducer;
