import { FETCH_PRODUCT, FETCH_CATEGORY, FETCH_BOOKING, FETCH_CUSTOMER_LIST } from '../utils/constants';

const initState = {
    products: [],
    categories: [],
    bookings: [],
    customerList: []
}

const reducer = (state = initState, action) => {
    switch (action.type) {
        case FETCH_PRODUCT:
            return {
                ...state,
                products: action.payload
            }
        case FETCH_CATEGORY:
            return {
                ...state,
                categories: action.payload
            }
        case FETCH_BOOKING:
            return {
                ...state,
                bookings: action.payload
            }
        case FETCH_CUSTOMER_LIST:
            return {
                ...state,
                customerList: action.payload
            }
        default:
            return state;
    }
}

export default reducer;
