import {
  FETCH_PRODUCT,
  GET_ONE_PRODUCT,
  FETCH_CATEGORY,
  FETCH_BOOKING,
  FETCH_CUSTOMER_LIST,
  ADD_PRODUCT,
  CHANGE_PRODUCT,
  DELETE_PRODUCT,
} from "../utils/constants";
// import swal from 'sweetalert';
import api from "../api/api";

export const fetchProducts = () => {
  return async (dispatch) => {
    try {
      const response = await api.get("product");
      if (response) {
        dispatch({ type: FETCH_PRODUCT, payload: response.data });
      }
    } catch (error) {
      console.log(error, `<<<<<<<<< error`);
    }
  };
};

export const getOneProducts = (id) => {
  return async (dispatch) => {
    try {
      const response = await api.get(`product/${id}`);
      if (response) {
        dispatch({ type: GET_ONE_PRODUCT, payload: response.data });
      }
    } catch (error) {
      console.log(error, `<<<<<<<<< error`);
    }
  };
};

export const addProduct = (payload) => {
  return async (dispatch) => {
    try {
      const response = await api.post("product", payload);
      if (response) {
        dispatch({ type: ADD_PRODUCT, payload: response.data });
      }
    } catch (error) {
      console.log(error, `<<< error add product`);
    }
  };
};

export const changeProduct = (payload, id) => {
  return async (dispatch) => {
    try {
      const response = await api.put(`product/${id}`, payload);
      if (response) {
        dispatch({ type: CHANGE_PRODUCT, payload: response.data });
      }
    } catch (error) {
      console.log(error, `<<< error add product`);
    }
  };
};

export const deleteProduct = (id) => {
  return async (dispatch) => {
    try {
      const response = await api.delete(`product/${id}`);
      if (response) {
        dispatch({ type: DELETE_PRODUCT });
        dispatch(fetchProducts());
      }
    } catch (error) {
      console.log(error, `<<< error add product`);
    }
  };
};

export const fetchCategories = () => {
  return async (dispatch) => {
    try {
      const response = await api.get("category");
      if (response) {
        dispatch({ type: FETCH_CATEGORY, payload: response.data });
      }
    } catch (error) {
      console.log(error, `<<<<<<<<< error`);
    }
  };
};

export const fetchBookings = () => {
  return async (dispatch) => {
    try {
      const response = await api.get("booking");
      if (response) {
        dispatch({ type: FETCH_BOOKING, payload: response.data });
      }
    } catch (error) {
      console.log(error, `<<<<<<<<< error`);
    }
  };
};

export const fetchCustomerList = () => {
  return async (dispatch) => {
    try {
      const response = await api.get("customer");
      if (response) {
        dispatch({ type: FETCH_CUSTOMER_LIST, payload: response.data });
      }
    } catch (error) {
      console.log(error, `<<<<<<<<< error`);
    }
  };
};
